import { LOGIN_USER, LOGOUT_USER } from "./authTypes";

export default (work = [], action) => {
  switch (action.type) {
    case LOGIN_USER:
      return work;
    case LOGOUT_USER:
      return work;
    default:
      return work;
  }
};
