// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

// import redux
import { getBlog } from "../../../redux/blog/blogReducer";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Card4 } from "../../utils/cards";

// import helpers
import { getHeight } from "../../helpers";

// import media
import loader from "../../../media/loader.svg";

const Blog = () => {
  const [blogItems, setBlog] = useState([]);

  const dispatch = useDispatch();

  const { blog, isLoading, isError, message } = useSelector(
    (state) => state.blog
  );

  useEffect(() => {
    dispatch(getBlog());
  }, []);

  const content = (
    <div className="blog-grid" style={{ maxHeight: `${getHeight(70, 100)}px` }}>
      <img className="loader" src={loader} alt="loader" />
      {blogItems.length < 1 ? (
        blogItems.map((i) => (
          <Card4
            createdAt={i.createdAt}
            image={i.image}
            key={i._id}
            link={i._id}
            subtitle={i.subtitle}
            tags={i.tags}
            text={i.text}
            title={i.title}
          />
        ))
      ) : (
        <img src={loader} alt="loader" />
      )}
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Blog;
