// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import PublicRoute from "../../utils/PublicRoute.js";

// import helpers
import { getHeight } from "../../helpers";

const About = () => {
  const [profile, setProfile] = useState({});

  const getProfile = async () => {
    const myProfile = await axios.get("http://localhost:5000/profile");

    setProfile(myProfile.data[0]);
  };

  useEffect(() => {
    getProfile();
  }, []);

  console.log(profile);

  const {
    avatar,
    bio,
    codepen,
    email,
    facebook,
    github,
    linkedin,
    location,
    name,
    phone,
    twitter,
    youtube,
  } = profile;

  const content = (
    <div className="about" style={{ maxHeight: `${getHeight(70, 100)}px` }}>
      <div className="about-header">
        <div className="about-header-left">
          <img src={avatar} alt="profile avatar" />
        </div>
        <div className="about-header-right">
          <div className="about-header-keys">
            <p className="about-key">name</p>
            <p className="about-key">location</p>
            <p className="about-key">email</p>
          </div>
          <div className="about-header-details">
            <p>{name}</p>
            <p>{location}</p>
            <p>{email}</p>
            <Link to="/contact">mail me</Link>
            <a href={`tel:${phone}`}>{phone} phone me </a>
          </div>
        </div>
      </div>

      <a href={github} target="_blank">
        github
      </a>
      <a href={linkedin} target="_blank">
        linkedin
      </a>
      <a href={twitter} target="_blank">
        twitter
      </a>
      <a href={codepen} target="_blank">
        codepen
      </a>
      <a href={youtube} target="_blank">
        youtube
      </a>
      <a href={facebook} target="_blank">
        facebook
      </a>
      <p>{bio}</p>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default About;
