// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3, Input4 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    codeLink: "",
    image: "",
    logos: [],
    siteLink: "",
    subtitle: "",
    text: "",
    title: "",
  });

  const { codeLink, image, logos, siteLink, subtitle, text, title } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleCheckClick = (i) => {
    let currentArray = logos;

    if (currentArray.includes(i)) {
      console.log("already applied");
    } else {
      console.log(logos);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1 label="title" onChange={handleChange("title")} />
          <Input1 label="subtitle" onChange={handleChange("subtitle")} />
          <Input1 label="codeLink" onChange={handleChange("codeLink")} />
          <Input1 label="siteLink" onChange={handleChange("siteLink")} />
          <Input2 label="text" onChange={handleChange("text")} />
          <Input3 label="image" onChange={handleChange("image")} />
          <div className="checkboxes">
            <Input4
              onClick={() => {
                handleCheckClick("html");
              }}
              text="html"
            />
            <Input4
              onClick={() => {
                handleCheckClick("css");
              }}
              text="css"
            />
            <Input4
              onClick={() => {
                handleCheckClick("sass");
              }}
              text="sass"
            />
            <Input4
              onClick={() => {
                handleCheckClick("less");
              }}
              text="less"
            />
            <Input4
              onClick={() => {
                handleCheckClick("psql");
              }}
              text="psql"
            />
            <Input4
              onClick={() => {
                handleCheckClick("mongo");
              }}
              text="mongo"
            />
            <Input4
              onClick={() => {
                handleCheckClick("express");
              }}
              text="express"
            />
            <Input4
              onClick={() => {
                handleCheckClick("react");
              }}
              text="react"
            />
            <Input4
              onClick={() => {
                handleCheckClick("react-native");
              }}
              text="react-native"
            />
            <Input4
              onClick={() => {
                handleCheckClick("node");
              }}
              text="node"
            />
            <Input4
              onClick={() => {
                handleCheckClick("python");
              }}
              text="python"
            />
            <Input4
              onClick={() => {
                handleCheckClick("webpack");
              }}
              text="webpack"
            />
          </div>
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} title="New Work" />;
};

export default CreateBlog;
