// import dependencies
import { useState } from "react";

// import components
import PublicRoute from "../utils/PublicRoute.js";

// import styles
import "./style.min.css";

const Work = () => {
  const [state, setState] = useState({
    sitesActive: true,
    appsActive: true,
    gamesActive: true,
    miscActive: true,
  });

  const { sitesActive, appsActive, gamesActive, miscActive } = state;

  const sitesClick = () => {
    if ((sitesActive, appsActive, gamesActive, miscActive)) {
      setState({
        sitesActive: true,
        appsActive: false,
        gamesActive: false,
        miscActive: false,
      });
    }
  };
  const appsClick = () => {
    if ((sitesActive, appsActive, gamesActive, miscActive)) {
      setState({
        sitesActive: true,
        appsActive: false,
        gamesActive: false,
        miscActive: false,
      });
    }
  };
  const gamesClick = () => {
    if ((sitesActive, appsActive, gamesActive, miscActive)) {
      setState({
        sitesActive: true,
        appsActive: false,
        gamesActive: false,
        miscActive: false,
      });
    }
  };
  const miscClick = () => {
    if ((sitesActive, appsActive, gamesActive, miscActive)) {
      setState({
        sitesActive: true,
        appsActive: false,
        gamesActive: false,
        miscActive: false,
      });
    }
  };

  const catClick = (c) => {
    switch (c) {
      case "sites":
        sitesClick();
        break;
      case "apps":
        break;
      case "games":
        break;
      case "misc":
        break;
      default:
        break;
    }
  };

  const content = (
    <>
      <div className="work-options">
        <div
          className={`work-option ${sitesActive && "work-option-active"}`}
          onClick={() => catClick("sites")}
        >
          SITES
        </div>
        <div
          className={`work-option ${appsActive && "work-option-active"}`}
          onClick={() => catClick("apps")}
        >
          APPS
        </div>
        <div
          className={`work-option ${gamesActive && "work-option-active"}`}
          onClick={() => catClick("games")}
        >
          GAMES
        </div>
        <div
          className={`work-option ${miscActive && "work-option-active"}`}
          onClick={() => catClick("misc")}
        >
          MISC
        </div>
      </div>
    </>
  );

  return <PublicRoute content={content} />;
};

export default Work;
