// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";

// import utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Button3 } from "../../utils/buttons";
import { Input1, Input2 } from "../../utils/inputs";

// import helpers
import { isEmail, isPhoneNumber } from "../../helpers";

// import styles
import "./style.min.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    email: "elliotalderson@allsafe.org",
    name: "Elliot Alderson",
    phone: "07745558280",
    text: "test content",
    title: "Hello Friend",
  });
  const [state, setState] = useState({
    btnDisabled: false,
    btnText: "submit",
    message: "",
    showMessage: false,
  });

  const { email, name, phone, text, title } = formData;
  const { btnDisabled, btnText, message, showMessage } = state;

  useEffect(() => {
    if (email === "" || phone === "" || text === "" || title === "") {
      setState({ ...state, message: "please complete all fields" });
    } else if (!isEmail(email)) {
      setState({ ...state, message: "please enter a valid email" });
    } else if (!isPhoneNumber(phone)) {
      setState({ ...state, message: "please enter a valid phone number" });
    } else {
      setState({ ...state, btnDisabled: false, message: "" });
    }
  }, [text, email, phone, title]);

  const handleChange = (name) => (e) => {
    setFormData({ ...formData, [name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (message.length > 0) {
      setState({ ...state, showMessage: true });

      setTimeout(() => {
        setState({ ...state, showMessage: false });
      }, 3000);
    } else {
      try {
        setState({ ...state, btnDisabled: true, btnText: "submitting..." });

        axios.post("http://localhost:5000/mail", formData);

        setState({
          ...state,
          btnText: "submitted",
          message: "thank you for getting in touch",
          showMessage: true,
        });

        setTimeout(() => {
          setState({
            ...state,
            btnDisabled: false,
            btnText: "submit",
            message: "",
            showMessage: false,
          });

          setFormData({
            email: "",
            phone: "",
            text: "",
            title: "",
          });
        }, 2000);
      } catch (e) {
        setState({
          ...state,
          btnDisabled: false,
          btnText: "submit",
          message: "message error",
          showMessage: true,
        });

        setTimeout(() => {
          setState({
            ...state,
            message: "",
            showMessage: false,
          });
        }, 2000);
      }
    }
  };

  const content = (
    <div className="contact">
      <form onSubmit={handleSubmit}>
        <Input1
          label="email title"
          onChange={handleChange("title")}
          placeholder="enter title"
          value={title}
        />
        <Input1
          label="your email address"
          onChange={handleChange("email")}
          placeholder="enter email"
          value={email}
        />
        <Input1
          label="your phone number"
          onChange={handleChange("phone")}
          placeholder="enter phone number"
          value={phone}
        />
        <Input2
          label="email body"
          onChange={handleChange("text")}
          placeholder="enter email text"
          value={text}
        />
        <Button3
          disabled={btnDisabled}
          message={message}
          showMessage={showMessage}
          text={btnText}
        />
      </form>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Contact;
