// import dependencies
import axios from "axios";
import { useState } from "react";
import FileBase from "react-file-base64";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links";
import PrivateRoute from "../../utils/PrivateRoute";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    body:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam ac luctus tortor. Nunc sed quam felis. Quisque vitae imperdiet nisl, vitae rhoncus augue. Aenean commodo placerat mauris, in tempor augue blandit a. Praesent non efficitur ante. Fusce sit amet sapien sagittis, blandit augue ac, fringilla est. Curabitur consequat tortor vitae augue vehicula, condimentum sodales purus lobortis. Nunc ac lobortis neque. Sed tortor risus, auctor sed nibh eu, varius efficitur velit. Nulla facilisi. Vivamus facilisis, erat non dignissim facilisis, neque diam faucibus ligula, eu fermentum dui felis non lacus. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Nunc eget sem nulla. Fusce at elit tincidunt, volutpat orci in, cursus lectus. Aliquam nec ullamcorper nisl. In cursus commodo ipsum, nec vulputate justo. Quisque tincidunt, elit sed sodales tincidunt, libero elit sollicitudin tellus, a convallis arcu sapien eu velit. Praesent at ante quis ex accumsan vestibulum semper id ligula. Phasellus eros augue, aliquam eu eros eu, consectetur suscipit diam. Vestibulum vitae accumsan sapien. Quisque non libero sem. Duis varius, risus quis luctus luctus, purus sem dictum diam, eget iaculis quam mauris quis urna. Mauris iaculis faucibus elit eu convallis. Mauris quis mauris vitae lorem lobortis tincidunt quis in sapien. Nunc non diam consectetur, aliquet augue et, auctor ante. Maecenas mollis elit ante, at interdum neque convallis a. Nam sit amet porta sapien. Suspendisse potenti. Sed nec libero in libero varius fermentum. Vivamus quis mollis felis. Aenean iaculis urna quis molestie aliquet. Donec ex risus, vulputate quis orci facilisis, fringilla ultricies orci. Mauris feugiat nec risus id pharetra. Suspendisse eu nunc sed enim dictum vulputate. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Duis vitae leo sed nibh sodales volutpat pharetra nec diam. Phasellus at tempor eros, tristique faucibus sem. Mauris pretium, est id porta pulvinar, massa purus congue orci, in tristique orci nunc quis ex. Praesent vestibulum dui ligula, sed vestibulum mi viverra id. Aenean ornare ultrices justo, a scelerisque ante finibus eget. Nullam sed purus in magna egestas elementum a vel nunc. Sed convallis odio nec mattis malesuada. Phasellus eget felis aliquam tortor mollis facilisis. Nam iaculis tristique felis nec fermentum. Aliquam erat volutpat. Suspendisse sapien nibh, sodales id molestie id, volutpat vitae ante. Nullam nec luctus ante. Cras sollicitudin dui sit amet metus aliquet, ut pharetra urna efficitur. Nulla at mattis nisl. Curabitur posuere dui libero, quis placerat massa dictum eu. Sed molestie dignissim felis sed convallis. Sed consequat a risus non lobortis. Quisque sodales magna in purus rhoncus, a sodales dolor dapibus. Etiam massa velit, pellentesque id nunc et, semper pharetra magna. Interdum et malesuada fames ac ante ipsum primis in faucibus.",
    image: "",
    subtitle: "blog subtitle1",
    tags: "code,coffee,selftaught",
    title: "blog title1",
  });

  const { body, image, subtitle, tags, title } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);

    axios.post("http://localhost:5000/blog", formData, {
      headers: {
        authtoken: localStorage.user.token,
      },
    });
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1
            label="title"
            onChange={handleChange("title")}
            value={title}
          />
          <Input1
            label="subtitle"
            onChange={handleChange("subtitle")}
            value={subtitle}
          />
          <Input2 label="body" onChange={handleChange("body")} value={body} />
          <Input1 label="tags" onChange={handleChange("tags")} value={tags} />
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) => setFormData({ ...formData, image: base64 })}
          />
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} />;
};

export default CreateBlog;
