// import dependencies
import { useParams } from "react-router-dom";

// import components
// utils
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

const BlogItem = () => {
  const pageHeight = window.innerHeight - (window.innerWidth < 767 ? 60 : 95);

  const content = (
    <div className="blog-item" style={{ height: `${pageHeight}px` }}>
      <Navlink2 colour="#d0491e" link="/" text="&#9756; return to blog grid" />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default BlogItem;
