// import dependencies
import { useState } from "react";

// import data
import { work } from "./data";
// import helpers
import { shuffle } from "../helpers";

// import components
// utils
import PublicRoute from "../utils/PublicRoute.js";
import { Card1 } from "../utils/cards.js";

// import styles
import "./style.min.css";

const Work = () => {
  const [state, setState] = useState({
    sitesActive: true,
    appsActive: true,
    gamesActive: true,
    miscActive: true,
  });

  const { sitesActive, appsActive, gamesActive, miscActive } = state;

  const sitesClick = () => {
    if (
      (sitesActive && appsActive && gamesActive && miscActive) ||
      sitesActive
    ) {
      setState({
        sitesActive: true,
        appsActive: false,
        gamesActive: false,
        miscActive: false,
      });
    } else {
      setState({ ...state, sitesActive: true });
    }
  };
  const appsClick = () => {
    if (
      (sitesActive && appsActive && gamesActive && miscActive) ||
      appsActive
    ) {
      setState({
        sitesActive: false,
        appsActive: true,
        gamesActive: false,
        miscActive: false,
      });
    } else {
      setState({ ...state, appsActive: true });
    }
  };
  const gamesClick = () => {
    if (
      (sitesActive && appsActive && gamesActive && miscActive) ||
      gamesActive
    ) {
      setState({
        sitesActive: false,
        appsActive: false,
        gamesActive: true,
        miscActive: false,
      });
    } else {
      setState({ ...state, gamesActive: true });
    }
  };
  const miscClick = () => {
    if (
      (sitesActive && appsActive && gamesActive && miscActive) ||
      miscActive
    ) {
      setState({
        sitesActive: false,
        appsActive: false,
        gamesActive: false,
        miscActive: true,
      });
    } else {
      setState({ ...state, miscActive: true });
    }
  };

  const catClick = (c) => {
    switch (c) {
      case "sites":
        sitesClick();
        break;
      case "apps":
        appsClick();
        break;
      case "games":
        gamesClick();
        break;
      case "misc":
        miscClick();
        break;
      default:
        break;
    }
  };

  const shuffledWork = shuffle(work);

  const content = (
    <>
      <div className="work-options">
        <div
          className={`work-option ${sitesActive && "work-option-active"}`}
          onClick={() => catClick("sites")}
        >
          SITES
        </div>
        <div
          className={`work-option ${appsActive && "work-option-active"}`}
          onClick={() => catClick("apps")}
        >
          APPS
        </div>
        <div
          className={`work-option ${gamesActive && "work-option-active"}`}
          onClick={() => catClick("games")}
        >
          GAMES
        </div>
        <div
          className={`work-option ${miscActive && "work-option-active"}`}
          onClick={() => catClick("misc")}
        >
          MISC
        </div>
      </div>
      <div className="work-grid">
        {shuffledWork.map((i) => (
          <Card1 category={i.category} title={i.title} />
        ))}
      </div>
    </>
  );

  return <PublicRoute content={content} />;
};

export default Work;
