// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import FileBase from "react-file-base64";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink4 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

// import helpers
import { getHeight } from "../../helpers";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    avatar: "",
    bio: "",
    email: "",
    location: "",
    name: "",
    phone: "",
    linkedin: "",
    github: "",
    youtube: "",
    codepen: "",
    twitter: "",
    facebook: "",
  });
  const [state, setState] = useState({
    btnDisabled: false,
  });

  const {
    avatar,
    bio,
    email,
    location,
    name,
    phone,
    linkedin,
    github,
    youtube,
    codepen,
    twitter,
    facebook,
  } = formData;
  const { btnDisabled } = state;

  useEffect(() => {
    if (
      avatar === "" ||
      bio === "" ||
      email === "" ||
      location === "" ||
      name === "" ||
      phone === "" ||
      linkedin === "" ||
      github === "" ||
      youtube === "" ||
      codepen === "" ||
      twitter === "" ||
      facebook === ""
    ) {
      setState({ ...state, btnDisabled: true });
    } else {
      setState({ ...state, btnDisabled: false });
    }
  }, [
    avatar,
    bio,
    email,
    location,
    name,
    phone,
    linkedin,
    github,
    youtube,
    codepen,
    twitter,
    facebook,
  ]);

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    setFormData({
      avatar: "",
      bio: " ",
      location: "",
      name: "",
      phone: "",
      linkedin: "",
      github: "",
      youtube: "",
      codepen: "",
      twitter: "",
      facebook: "",
    });

    axios.post("http://localhost:5000/profile", formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div
          className="form-body"
          style={{ maxHeight: `${getHeight(130, 165)}px` }}
        >
          <Input1 label="name" onChange={handleChange("name")} value={name} />
          <Input1
            label="location"
            onChange={handleChange("location")}
            value={location}
          />
          <Input1
            label="email"
            onChange={handleChange("email")}
            value={email}
          />
          <Input1
            label="phone"
            onChange={handleChange("phone")}
            value={phone}
          />
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) =>
              setFormData({ ...formData, avatar: base64 })
            }
          />
          <Input2 label="bio" onChange={handleChange("bio")} value={bio} />
          <Input1
            label="linkedin"
            onChange={handleChange("linkedin")}
            value={linkedin}
          />
          <Input1
            label="github"
            onChange={handleChange("github")}
            value={github}
          />
          <Input1
            label="youtube"
            onChange={handleChange("youtube")}
            value={youtube}
          />
          <Input1
            label="codepen"
            onChange={handleChange("codepen")}
            value={codepen}
          />
          <Input1
            label="twitter"
            onChange={handleChange("twitter")}
            value={twitter}
          />
          <Input1
            label="facebook"
            onChange={handleChange("facebook")}
            value={facebook}
          />
        </div>
        <Button1 disabled={btnDisabled} text="SUBMIT" />
      </form>
      <Navlink4 link="/dashboard" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} />;
};

export default CreateBlog;
