// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

// import redux
import { getSkills } from "../../../redux/skills/skillsReducer";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Card2 } from "../../utils/cards.js";

// import helpers
import { getBulletPoints, getHeight, shuffle } from "../../helpers";

const Skills = () => {
  let shuffledItems = [
    { test: "test1", subtitle: "subtitle1" },
    { test: "test2", subtitle: "subtitle2" },
    { test: "test3", subtitle: "subtitle3" },
    { test: "test4", subtitle: "subtitle4" },
  ];
  const dispatch = useDispatch();

  const skills = useSelector((state) => state.skills);

  useEffect(() => {
    dispatch(getSkills());
  }, []);

  // shuffledItems = skills.skills;

  shuffledItems = shuffle(shuffledItems);

  console.log(shuffledItems);
  // console.log(items);

  // bulletPoints={getBulletPoints(i.bulletpoints)}

  const content = (
    <div
      className="skills-grid"
      style={{
        maxHeight: getHeight(55, 100),
        minHeight: getHeight(55, 100),
      }}
    >
      {shuffledItems.map((i) => (
        <Card2 key={i._id} logo={i.logo} name={i.name} />
      ))}
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Skills;
