// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import PublicRoute from "../../utils/PublicRoute.js";

// import helpers
import { getHeight } from "../../helpers";

const About = () => {
  const [profile, setProfile] = useState({});

  const getProfile = async () => {
    const myProfile = await axios.get("http://localhost:5000/profile");

    setProfile(myProfile.data[0]);
  };

  useEffect(() => {
    getProfile();
  }, []);

  console.log(profile);

  const {
    avatar,
    bio,
    codepen,
    email,
    facebook,
    github,
    linkedin,
    location,
    name,
    phone,
    twitter,
    youtube,
  } = profile;

  const content = (
    <div className="about" style={{ maxHeight: `${getHeight(70, 100)}px` }}>
      <img src={avatar} alt="profile avatar" />
      <p>{name}</p>
      <p>{location}</p>
      <p>{email}</p>
      <Link to="/contact">mail me</Link>
      <a href={`tel:${phone}`}>phone me</a>
      <a href={github}>github</a>
      <a href={linkedin}>linkedin</a>
      <a href={twitter}>twitter</a>
      <a href={codepen}>codepen</a>
      <a href={youtube}>youtube</a>
      <a href={facebook}>facebook</a>
      <p>{bio}</p>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default About;
