// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links";
import PrivateRoute from "../../utils/PrivateRoute";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    body: "",
    image: "",
    subtitle: "",
    tags: "",
    title: "",
  });

  const { body, image, subtitle, tags, title } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const content = (
    <>
      <form onSubmit={handleSubmit}>
        <Input1 label="title" onChange={handleChange("title")} value={title} />
        <Input1 label="subtitle" onChange={handleChange("")} value={subtitle} />
        <Input2 label="body" onChange={handleChange("")} value={body} />
        <Input1 label="tags" onChange={handleChange("")} value={tags} />
        <Input3 label="image" onChange={handleChange("")} value={image} />
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 text="return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} title="New Blog" />;
};

export default CreateBlog;
