import {
  CREATE_SKILL,
  DELETE_SKILL,
  READ_SKILLS,
  READ_SKILL_ITEM,
  UPDATE_SKILL,
} from "./skillsTypes";

export default (skills = [], action) => {
  switch (action.type) {
    case CREATE_SKILL:
      return skills;
    case DELETE_SKILL:
      return skills;
    case READ_SKILLS:
      return skills;
    case UPDATE_SKILL:
      return skills;
    default:
      return skills;
  }
};
