// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input4 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links";
import PrivateRoute from "../../utils/PrivateRoute";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    body: "",
    image: "",
    subtitle: "",
    tags: "",
    title: "",
  });

  const { body, image, subtitle, tags, title } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1
            label="title"
            onChange={handleChange("title")}
            value={title}
          />
          <Input1
            label="subtitle"
            onChange={handleChange("subtitle")}
            value={subtitle}
          />
          <Input2 label="body" onChange={handleChange("body")} value={body} />
          <Input1 label="tags" onChange={handleChange("tags")} value={tags} />
          <div className="checkboxes">
            <h4>select image</h4>
            <Input4
              onChange={() => {
                handleCheckClick("html");
              }}
              text="code"
            />
          </div>
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} />;
};

export default CreateBlog;
