import { useState } from "react";
import { Routes, Route } from "react-router-dom";

import nameLogo from "./media/name-logo.svg";

import "./style.min.css";

import Header from "./components/layout/Header.js";
import Landing from "./components/layout/Landing.js";

import About from "./components/routes/About.js";
import Blog from "./components/routes/Blog.js";
import Contact from "./components/routes/Contact.js";
import Skills from "./components/routes/Skills.js";
import Work from "./components/routes/Work.js";

import WorkItem from "./components/routes/WorkItem.js";

import crossIcon from "./media/cross.svg";
import menuIcon from "./media/menu.svg";

const App = () => {
  const [landing, showLanding] = useState(true);
  const [fadeOut, triggerFadeOut] = useState(false);

  return (
    <div className="app">
      {landing ? (
        <Landing
          onClick={() => {
            triggerFadeOut(true);
            setTimeout(() => {
              showLanding(false);
            }, 700);
          }}
          slideOut={fadeOut}
        />
      ) : (
        <>
          <Header />
          <Routes>
            <Route exact path="/about" element={<About />} />
            <Route exact path="/blog" element={<Blog />} />
            <Route exact path="/contact" element={<Contact />} />
            <Route exact path="/skills" element={<Skills />} />
            <Route exact path="/" element={<Work />} />

            <Route path="/work" element={<WorkItem />} />
          </Routes>
        </>
      )}
    </div>
  );
};

export default App;
