// import components
// utils
import { Card3 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

// media - HARD CODE
import avatar from "../../../media/avatar2.jpg";
import mongoLogo from "../../../media/mongo-logo2.svg";
import expressLogo from "../../../media/express-logo2.svg";
import reactLogo from "../../../media/react-logo2.svg";
import nodeLogo from "../../../media/node-logo2.svg";

const WorkItem = () => {
  // const itemName = location.pathname.substring(6, location.pathname.length);
  // let item = work.filter((i) => i.title === itemName);
  // item = item[0];

  const pageHeight = window.innerHeight - (window.innerWidth < 767 ? 60 : 95);

  const content = (
    <div className="work-item" style={{ height: `${pageHeight}px` }}>
      <Navlink2 colour="#d0491e" text="&#9756; return to work grid" />
      <Card3
        image={avatar}
        logos={[mongoLogo, expressLogo, reactLogo, nodeLogo]}
        subtitle="subtitle for app1"
        text="  Ut eu eros fermentum, feugiat turpis a, finibus lectus. Vestibulum
          et odio sit amet elit pulvinar rhoncus id lobortis nibh. Donec velit
          sem, pulvinar ut pretium sit amet, efficitur vel urna. Duis nec
          lorem fringilla nunc gravida ultricies. Proin nec finibus quam.
          Pellentesque elementum orci justo, ac efficitur lorem mollis sit
          amet. Nullam tincidunt malesuada elit non condimentum. Mauris rutrum
          scelerisque sapien id posuere. Donec elementum a ante facilisis
          congue. Aliquam laoreet accumsan quam et condimentum. Suspendisse
          pulvinar ipsum vitae leo ultrices, nec auctor velit pulvinar. Class
          aptent taciti sociosqu ad litora torquent per conubia nostra, per
          inceptos himenaeos."
        title="app1"
      />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default WorkItem;
