// import dependencies
import { useEffect, useState } from "react";

// import utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Button3 } from "../../utils/buttons";
import { Input1, Input2 } from "../../utils/inputs";

// import helpers
import { isEmail, isPhoneNumber } from "../../helpers";

// import styles
import "./style.min.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    body: "",
    email: "",
    phone: "",
    title: "",
  });
  const [state, setState] = useState({
    btnText: "submit",
    message: "",
    showMessage: false,
  });

  const { body, email, phone, title } = formData;
  const { btnText, error, showMessage } = state;

  useEffect(() => {
    if (body === "" || email === "" || phone === "" || title === "") {
      setState({ ...state, message: "please complete all fields" });
    } else if (!isEmail(email)) {
      setState({ ...state, message: "please enter a valid email" });
    } else if (!isPhoneNumber(phone)) {
      setState({ ...state, message: "please enter a valid phone number" });
    } else {
      setState({ ...state, message: "" });
    }
  }, [body, email, phone, title]);

  const handleChange = (name) => (e) => {
    setFormData({ ...formData, [name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (error.length > 0) {
      setState({ ...state, showMessage: true });

      setTimeout(() => {
        setState({ ...state, showMessage: false });
      }, 3000);
    } else {
      setState({
        ...state,
        message:
          "Email sent, thank you for getting in touch and I will be in respond promptly",
      });

      setTimeout(() => {
        setState({ ...state, showMessage: false });
      }, 3000);
    }
  };

  const content = (
    <div className="contact">
      <form onSubmit={handleSubmit}>
        <Input1
          label="email title"
          onChange={handleChange("title")}
          placeholder="enter title"
          value={title}
        />
        <Input1
          label="your email address"
          onChange={handleChange("email")}
          placeholder="enter email"
          value={email}
        />
        <Input1
          label="your phone number"
          onChange={handleChange("phone")}
          placeholder="enter phone number"
          value={phone}
        />
        <Input2
          label="email body"
          onChange={handleChange("body")}
          placeholder="enter email body"
          value={body}
        />
        <Button3 text={btnText} error={error} showMessage={showMessage} />
      </form>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Contact;
