// import dependencies
import axios from "axios";
import { useState } from "react";
import { Navigate } from "react-router-dom";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute";
import { Button1 } from "../../utils/buttons";
import { Input1 } from "../../utils/inputs";

const SignIn = () => {
  const [formData, setFormData] = useState({
    email: "dougiehawes@hotmail.com",
    password: "Wittgenstein42",
  });
  const [auth, setAuth] = useState(false);

  const { email, password } = formData;

  const onChange = (name) => (e) => {
    setFormData({ ...formData, [name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://localhost:5000/auth/login", formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <Input1 label="email" onChange={onChange("email")} value={email} />
        <Input1
          label="password"
          onChange={onChange("password")}
          type="password"
          value={password}
        />
        <Button1 text="ENTER" />
      </form>
      {auth && <Navigate replace to="/dashboard" />}
    </>
  );

  return <PublicRoute content={content} />;
};

export default SignIn;
