// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";

// import redux
import { getBlogItem } from "../../../redux/blog/blogReducer";

// import components
// utils
import { Card5 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

// import helpers
import { getHeight } from "../../helpers";

// import media
import loader from "../../../media/loader.svg";

const BlogItem = () => {
  const [blogItem, setBlogItem] = useState({});
  const { id } = useParams();

  const getBlogItem = async () => {
    const item = await axios.get(`${process.env.REACT_APP_API_URL}/blog/${id}`);

    setBlogItem(item.data);
  };

  useEffect(() => {
    getBlogItem();
  }, []);

  const { createdAt, image, subtitle, tags, text, title } = blogItem;

  const content = (
    <div
      className="blog-item"
      style={{
        maxHeight: getHeight(60, 100),
      }}
    >
      <Navlink2 link="/blog" text="&#9756; return to blog" />
      <Card5
        createdAt={createdAt}
        image={image}
        subtitle={subtitle}
        tags={tags}
        text={text}
        title={title}
      />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default BlogItem;
