import { LOGIN_FAILURE, LOGIN_SUCCESS, LOGOUT } from "./authTypes";

export default (work = [], action) => {
  switch (action.type) {
    case LOGIN_FAILURE:
    case LOGOUT:
      localStorage.removeItem("token");
      return {
        ...work,
        token: null,
        isAuthenticated: false,
        loading: false,
      };
    case LOGIN_SUCCESS:
      return work;

    default:
      return work;
  }
};
