// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import PublicRoute from "../../utils/PublicRoute.js";

import { Card3 } from "../../utils/cards";

const Blog = () => {
  const [blog, setBlog] = useState([]);

  const getBlog = async () => {
    const allBlog = await axios.get("http://localhost:5000/blog");

    setBlog(allBlog.data);
  };

  useEffect(() => {
    getBlog();
  }, []);

  console.log(blog);

  const content = (
    <>
      {blog.map((i) => (
        <div key={i._id}>
          <Link to={`/blog/${i._id}`}>
            <img src={i.image} alt={`image for ${i.title}`} />
            <h3>{i.title}</h3>
            <p>{i.subtitle}</p>
            <p>{i.createdAt}</p>
            <p>{i.text}</p>
          </Link>
        </div>
      ))}
    </>
  );

  return <PublicRoute content={content} />;
};

export default Blog;
