import Fade from "react-reveal/Fade";

import { capitalise } from "../helpers";

import "./style.min.css";

const PublicRoute = ({ title, content }) => {
  return (
    <Fade bottom cascade>
      <div
        className="route"
        style={{
          maxHeight: `${window.innerHeight - 90}px`,
          minHeight: `${window.innerHeight - 90}px`,
        }}
      >
        {title && <h2 className="route-title">{capitalise(title)}</h2>}
        <div className="route-content">{content}</div>
      </div>
    </Fade>
  );
};

export default PublicRoute;
