// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute";
import { Link1 } from "../../utils/links";

// import helpers
import { getHeight } from "../../helpers";

// media imports
import envelopeLogo from "../../../media/envelope.svg";
import phoneLogo from "../../../media/phone.svg";

import githubLogo from "../../../media/github-logo.svg";
import linkedinLogo from "../../../media/linkedin-logo.svg";
import youtubeLogo from "../../../media/youtube-logo.svg";

const About = () => {
  const [profile, setProfile] = useState({});

  const getProfile = async () => {
    const myProfile = await axios.get("http://localhost:5000/profile");

    setProfile(myProfile.data[0]);
  };

  useEffect(() => {
    getProfile();
  }, []);

  const {
    avatar,
    bio,
    codepen,
    email,
    facebook,
    github,
    linkedin,
    location,
    name,
    phone,
    twitter,
    youtube,
  } = profile;

  const content = (
    <div className="about" style={{ maxHeight: getHeight(60, 100) }}>
      <div className="about-header">
        <div className="about-header-left">
          <img className="about-avatar" src={avatar} alt="profile avatar" />
        </div>
        <div className="about-header-right">
          <div className="about-header-keys">
            <p className="about-key">name</p>
            <p className="about-key">location</p>
            <p className="about-key">email</p>
            <p className="about-key">phone</p>
          </div>
          <div className="about-header-details">
            <p className="about-detail">{name}</p>
            <p className="about-detail">{location}</p>
            <p className="about-detail">
              <span>{email}</span>
              <Link to="/contact">
                <img src={envelopeLogo} alt="email logo" />
              </Link>
            </p>
            <p className="about-detail">
              <span>0{phone ? phone.substring(3, phone.length) : null}</span>
              <a href={`tel:${phone}`}>
                <img src={phoneLogo} alt="phone logo" />
              </a>
            </p>
          </div>
        </div>
      </div>
      <div className="about-social">
        <Link1
          image={githubLogo}
          sitename="GitHub"
          to="https://github.com/DougieHawes"
        />
        <Link1
          image={linkedinLogo}
          sitename="linkedin"
          to="https://www.linkedin.com/in/dougiehawes"
        />
        <Link1
          image={githubLogo}
          sitename="Twitter"
          to="https://github.com/DougieHawes"
        />
        <Link1
          image={githubLogo}
          sitename="Codepen"
          to="https://github.com/DougieHawes"
        />
        <Link1
          image={youtubeLogo}
          sitename="youtube"
          to="https://www.youtube.com/channel/UCg6zwHai7Lig5CGcWkWfqTA"
        />
        <Link1
          image={githubLogo}
          sitename="Facebook"
          to="https://github.com/DougieHawes"
        />
      </div>
      <div className="about-bio-container">
        <h4 className="about-bio-title">BIO</h4>
        <p className="about-bio">{bio}</p>
      </div>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default About;
