// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    avatar: "",
    bio: "",
    location: "Huntingdon, Cambridgeshire",
    name: "Dougie Hawes",
    phone: "+447742148280",
    linkedin: "https://www.linkedin.com/in/dougiehawes/",
    github: "https://github.com/DougieHawes",
    youtube: "https://www.youtube.com/channel/UCg6zwHai7Lig5CGcWkWfqTA",
    codepen: "https://codepen.io/DougieHawes",
    twitter: "https://twitter.com/Dougie_Hawes",
    facebook: "",
  });

  const {
    avatar,
    bio,
    location,
    name,
    phone,
    linkedin,
    github,
    youtube,
    codepen,
    twitter,
    facebook,
  } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1 label="name" onChange={handleChange("name")} value={name} />
          <Input1
            label="location"
            onChange={handleChange("location")}
            value={location}
          />
          <Input1
            label="phone"
            onChange={handleChange("phone")}
            value={phone}
          />
          <Input2 label="bio" onChange={handleChange("bio")} value={bio} />
          <Input3
            label="avatar"
            onChange={handleChange("avatar")}
            value={avatar}
          />

          <Input1
            label="linkedin"
            onChange={handleChange("linkedin")}
            value={linkedin}
          />
          <Input1
            label="github"
            onChange={handleChange("github")}
            value={github}
          />
          <Input1
            label="youtube"
            onChange={handleChange("youtube")}
            value={youtube}
          />
          <Input1
            label="codepen"
            onChange={handleChange("codepen")}
            value={codepen}
          />
          <Input1
            label="twitter"
            onChange={handleChange("twitter")}
            value={twitter}
          />
          <Input1
            label="facebook"
            onChange={handleChange("facebook")}
            value={facebook}
          />
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} title="Update Profile" />;
};

export default CreateBlog;
