import { createSlice, creatAsyncThunk } from "@reduxjs/toolkit";

import { LOGIN_FAILURE, LOGIN_SUCCESS, LOGOUT } from "./authTypes";

const initialState = {
  user: null,
  isError: false,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_FAILURE:
    case LOGOUT:
      localStorage.removeItem("token");
      return {
        ...state,
        token: null,
        isAuthenticated: false,
        loading: false,
      };
    case LOGIN_SUCCESS:
      localStorage.setItem("token", payload.token);
      return {
        ...state,
        ...payload,
        isAuthenticated: true,
        loading: false,
      };
    default:
      return state;
  }
};
