// import dependencies
import axios from "axios";
import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import Fade from "react-reveal/Fade";

import "./style.min.css";

const PrivateRoute = ({ content, title }) => {
  const [auth, setAuth] = useState(false);

  const { user } = useSelector((state) => state.auth);

  const validate = async (t) => {
    const validated = await axios.get("http://localhost:5000/auth/isauth", {
      headers: {
        authtoken: t,
      },
    });
    console.log(validated.status === 200);
    setAuth(validated.status === 200);
  };

  useEffect(() => {
    if (user.token !== "") {
      validate(user.token);
    }
    console.log(auth);
  }, []);

  return (
    <Fade>
      <div className="route">
        <h3 className="route-title private-title">{title}</h3>
        <div className="route-content private-content">{content}</div>
        {auth ? null : <Navigate to="/" />}
      </div>
    </Fade>
  );
};

export default PrivateRoute;
