import { Link, useLocation } from "react-router-dom";

import titleLogo from "./media/title-logo.png";

import "./style.min.css";

const App = () => {
  const location = useLocation();

  return (
    <div className="app">
      <header className="header">
        <div className="header-container">
          <div className="title-box">
            <Link to="/">
              <img className="title-image" src={titleLogo} alt="" />
            </Link>
          </div>
          <nav className="navbar">
            <div className="navbar-container">
              <Link
                className={`nav-link ${
                  location.pathname == "/work" && "nav-link-active"
                }`}
                to="/work"
              >
                Work
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/skills" && "nav-link-active"
                }`}
                to="/skills"
              >
                Skills
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/about" && "nav-link-active"
                }`}
                to="/about"
              >
                About
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/contact" && "nav-link-active"
                }`}
                to="/contact"
              >
                Contact
                <div className="nav-link-underline"></div>
              </Link>
            </div>
          </nav>
        </div>
      </header>
    </div>
  );
};

export default App;
