// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3, Input4 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    codeLink: "",
    image: "",
    siteLink: "",
    subtitle: "",
    text: "",
    title: "",
  });

  const { codeLink, image, siteLink, title } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleCheckClick = (i) => {
    console.log(i);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1 label="title" onChange={handleChange("title")} />
          <Input1 label="subtitle" onChange={handleChange("subtitle")} />
          <Input1 label="codeLink" onChange={handleChange("codeLink")} />
          <Input1 label="siteLink" onChange={handleChange("siteLink")} />
          <Input2 label="text" onChange={handleChange("text")} />
          <Input3 label="image" onChange={handleChange("image")} />
          <div className="checkboxes">
            <Input4 handleCheckClick={handleCheckClick} text="html" />
            <Input4 handleCheckClick={handleCheckClick} text="css" />
            <Input4 handleCheckClick={handleCheckClick} text="sass" />
            <Input4 handleCheckClick={handleCheckClick} text="less" />
            <Input4 handleCheckClick={handleCheckClick} text="psql" />
            <Input4 handleCheckClick={handleCheckClick} text="mongo" />
            <Input4 handleCheckClick={handleCheckClick} text="express" />
            <Input4 handleCheckClick={handleCheckClick} text="react" />
            <Input4 handleCheckClick={handleCheckClick} text="react-native" />
            <Input4 handleCheckClick={handleCheckClick} text="node" />
            <Input4 handleCheckClick={handleCheckClick} text="python" />
            <Input4 handleCheckClick={handleCheckClick} text="webpack" />
          </div>
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} title="New Work" />;
};

export default CreateBlog;
