// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Card2 } from "../../utils/cards.js";

// import helpers
import { getHeight, shuffle } from "../../helpers";

const Skills = () => {
  const [items, setItems] = useState([]);

  const getItems = async () => {
    const foundItems = await axios.get("http://localhost:5000/skills");

    setItems(foundItems.data);
  };

  useEffect(() => {
    getItems();
  }, []);

  const getBulletPoints = (s) => s.split(",");

  const shuffledItems = shuffle(items);

  const content = (
    <div
      className="skills-grid"
      style={{
        maxHeight: getHeight(55, 100),
        minHeight: getHeight(55, 100),
      }}
    >
      {shuffledItems.map((i) => (
        <Card2
          bulletPoints={getBulletPoints(i.bulletpoints)}
          key={i._id}
          logo={i.logo}
          name={i.name}
        />
      ))}
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Skills;
