import { Link, useLocation } from "react-router-dom";

import { capitalise } from "../helpers";

export const Link1 = ({ sitename, to }) => (
  <a className="link" href={to} target="_blank">
    <i class={`fa-brands fa-${sitename}`}></i>
  </a>
);

export const Navlink1 = ({ link, text, toggleNavbar = toggleNavbar }) => (
  <Link
    className={`nav-link ${location.pathname == link && "nav-link-active"}`}
    to={link}
    onClick={screen.width <= 767 && toggleNavbar}
  >
    {capitalise(text)}
    <div className="nav-link-underline"></div>
  </Link>
);
