// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

// import redux
import { getProfile } from "../../../redux/profile/profileReducer";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute";
import { Link1 } from "../../utils/links";

// import helpers
import { getHeight } from "../../helpers";

// media imports
import envelopeLogo from "../../../media/envelope.svg";
import phoneLogo from "../../../media/phone.svg";

import codepenLogo from "../../../media/codepen-logo.svg";
import facebookLogo from "../../../media/facebook-logo.svg";
import githubLogo from "../../../media/github-logo.svg";
import linkedinLogo from "../../../media/linkedin-logo.svg";
import twitterLogo from "../../../media/twitter-logo.svg";
import youtubeLogo from "../../../media/youtube-logo.svg";

const About = () => {
  // const dispatch = useDispatch();
  //
  // const profile = useSelector((state) => state.profile.profile[0]);
  //
  // useEffect(() => {
  //   dispatch(getProfile());
  // }, []);

  const content = profile ? (
    <div className="about" style={{ maxHeight: getHeight(60, 100) }}>
      <div className="about-header">
        <div className="about-header-left">
          <img
            className="about-avatar"
            src={profile.avatar}
            alt="profile avatar"
          />
        </div>
        <div className="about-header-right">
          <div className="about-header-row">
            <p className="about-key">Name</p>
            <p className="about-detail">{profile.name}</p>
          </div>
          <div className="about-header-row">
            <p className="about-key">Location</p>
            <p className="about-detail">{profile.location}</p>
          </div>
          <div className="about-header-row">
            <p className="about-key">Email</p>
            <p className="about-detail">
              <Link to="/contact">{profile.email}</Link>
            </p>
          </div>
          <div className="about-header-row">
            <p className="about-key">Phone</p>
            <a
              className="about-detail"
              href={`tel:+44${
                profile.phone &&
                profile.phone.substring(1, profile.phone.length)
              }`}
            >
              {profile.phone}
            </a>
          </div>
        </div>
      </div>
      <div className="about-social">
        <Link1 image={githubLogo} sitename="GitHub" to={profile.github} />
        <Link1 image={linkedinLogo} sitename="Linkedin" to={profile.linkedin} />
        <Link1 image={twitterLogo} sitename="Twitter" to={profile.twitter} />
        <Link1 image={codepenLogo} sitename="Codepen" to={profile.codepen} />
        <Link1 image={youtubeLogo} sitename="YouTube" to={profile.youtube} />
        <Link1 image={facebookLogo} sitename="Facebook" to={profile.facebook} />
      </div>
      <div className="about-bio-container">
        <h4 className="about-bio-title">BIO</h4>
        <p className="about-bio">{profile.bio}</p>
      </div>
    </div>
  ) : (
    <>loading</>
  );

  return <PublicRoute content={content} />;
};

export default About;
