// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";

// import redux
import { getBlogItem } from "../../../redux/blog/blogReducer";

// import components
// utils
import { Card3 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

// import helpers
import { getHeight } from "../../helpers";

// import media
import loader from "../../../media/loader.svg";

const WorkItem = () => {
  const { id } = useParams();

  const dispatch = useDispatch();

  let blogItem = useSelector((state) => state.blog);

  useEffect(() => {
    dispatch(getBlogItem(id));
  }, []);

  blogItem = blogItem.blog;

  const content = (
    <div className="work-item">
      <Navlink2 colour="#d0491e" link="/" text="&#9756; return to all work" />
      <Card3
        codeLink={codeLink}
        image={image}
        logos={logos}
        siteLink={siteLink}
        subtitle={subtitle}
        text={text}
        title={title}
      />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default WorkItem;
