import { Link, useLocation } from "react-router-dom";

export const Navlink1 = ({ text, toggleNavbar }) => {
  const capitalise = (w) => {
    return w[0];
  };

  return (
    <Link
      className={`nav-link ${
        location.pathname == `/${text}` && "nav-link-active"
      }`}
      to={`/${text}`}
      onClick={screen.width <= 767 && toggleNavbar}
    >
      {capitalise(text)}
      <div className="nav-link-underline"></div>
    </Link>
  );
};
