// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

// import redux
import { getSkills } from "../../../redux/skills/skillsReducer";

// import components
// utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Card2 } from "../../utils/cards.js";

// import helpers
import { getHeight, shuffle } from "../../helpers";

// import media
import loader from "../../../media/loader.svg";

const Skills = () => {
  const dispatch = useDispatch();

  const skills = useSelector((state) => state.skills);

  useEffect(() => {
    dispatch(getSkills());
  }, []);

  let items = skills.skills;

  console.log(items);

  const getBulletPoints = (s) => s.split(",");

  const shuffledItems = items.length > 0 ? shuffle(items) : null;
  console.log(shuffledItems);

  const content = (
    <div
      className="skills-grid"
      style={{
        maxHeight: getHeight(55, 100),
        minHeight: getHeight(55, 100),
      }}
    >
      {items.map((i) => (
        <Card2
          bulletPoints={getBulletPoints(i.bulletpoints)}
          key={i._id}
          logo={i.logo}
          name={i.name}
        />
      ))}
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Skills;
