// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import PublicRoute from "../../utils/PublicRoute.js";

import { Card4 } from "../../utils/cards";

// import helpers
import { getHeight } from "../../helpers";

// import media
import loader from "../../../media/loader.svg";

const Blog = () => {
  const [blog, setBlog] = useState([]);

  useEffect(() => {}, []);

  const content = (
    <div className="blog-grid" style={{ maxHeight: `${getHeight(70, 100)}px` }}>
      {blog ? (
        blog.map((i) => (
          <Card4
            createdAt={i.createdAt}
            image={i.image}
            key={i._id}
            link={i._id}
            subtitle={i.subtitle}
            tags={i.tags}
            text={i.text}
            title={i.title}
          />
        ))
      ) : (
        <img src={loader} alt="loader" />
      )}
      blog.map((i) => (
      <Card4
        createdAt={i.createdAt}
        image={i.image}
        key={i._id}
        link={i._id}
        subtitle={i.subtitle}
        tags={i.tags}
        text={i.text}
        title={i.title}
      />
      ))
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Blog;
