// import dependencies
import axios from "axios";
import { useState } from "react";
import FileBase from "react-file-base64";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink4 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

// import helpers
import { getHeight } from "../../helpers";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    avatar: "",
    bio:
      "Nullam efficitur libero vitae neque dignissim, non condimentum magna elementum. Pellentesque tristique augue sed sollicitudin efficitur. Donec at egestas metus. In euismod magna nulla, in auctor lorem molestie vitae. Nam mi ex, scelerisque et est a, lacinia finibus ligula. Quisque convallis ligula nec enim iaculis condimentum. Mauris malesuada felis quis velit suscipit imperdiet. Pellentesque vel porta nisl. Quisque gravida sollicitudin sagittis. Vivamus sit amet euismod lectus, ac lacinia sapien. Quisque eu lacus nunc. Suspendisse potenti. Ut vestibulum neque vel ultrices vestibulum.",
    location: "Huntingdon, Cambridgeshire",
    name: "Dougie Hawes",
    phone: "+447742148280",
    linkedin: "https://www.linkedin.com/in/dougiehawes/",
    github: "https://github.com/DougieHawes",
    youtube: "https://www.youtube.com/channel/UCg6zwHai7Lig5CGcWkWfqTA",
    codepen: "https://codepen.io/DougieHawes",
    twitter: "https://twitter.com/Dougie_Hawes",
    facebook: "",
  });

  const {
    avatar,
    bio,
    email,
    location,
    name,
    phone,
    linkedin,
    github,
    youtube,
    codepen,
    twitter,
    facebook,
  } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://localhost:5000/profile", formData);
  };

  const content = (
    <div>
      <form className="form" onSubmit={handleSubmit}>
        <div
          className="form-body"
          style={{ maxHeight: `${getHeight(120, 160)}px` }}
        >
          <Input1 label="name" onChange={handleChange("name")} value={name} />
          <Input1
            label="location"
            onChange={handleChange("location")}
            value={location}
          />
          <Input1
            label="email"
            onChange={handleChange("email")}
            value={email}
          />
          <Input1
            label="phone"
            onChange={handleChange("phone")}
            value={phone}
          />
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) =>
              setFormData({ ...formData, avatar: base64 })
            }
          />
          <Input2 label="bio" onChange={handleChange("bio")} value={bio} />
          <Input1
            label="linkedin"
            onChange={handleChange("linkedin")}
            value={linkedin}
          />
          <Input1
            label="github"
            onChange={handleChange("github")}
            value={github}
          />
          <Input1
            label="youtube"
            onChange={handleChange("youtube")}
            value={youtube}
          />
          <Input1
            label="codepen"
            onChange={handleChange("codepen")}
            value={codepen}
          />
          <Input1
            label="twitter"
            onChange={handleChange("twitter")}
            value={twitter}
          />
          <Input1
            label="facebook"
            onChange={handleChange("facebook")}
            value={facebook}
          />
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink4 link="/dashboard" text="&#9756; return to dashboard" />
    </div>
  );

  return <PrivateRoute content={content} />;
};

export default CreateBlog;
