// import styles
import "./style.min.css";

export const Input1 = ({
  label,
  onChange,
  placeholder,
  type = "text",
  value,
}) => (
  <>
    <p className="input-label">{label}</p>
    <input
      className="input input1"
      onChange={onChange}
      placeholder={placeholder}
      type={type}
      value={value}
    />
  </>
);

export const Input2 = ({ label, onChange, placeholder, value }) => (
  <>
    <p className="input-label">{label}</p>
    <textarea
      className="input input2"
      onChange={onChange}
      placeholder={placeholder}
      value={value}
    />
  </>
);

export const Input3 = ({ label, onChange, placeholder, value }) => (
  <>
    <p className="input-label">{label}</p>
    <input
      className="input input3"
      onChange={onChange}
      placeholder={placeholder}
      type="file"
      value={value}
    />
  </>
);
