// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// import components
// utils
import { Card5 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

const BlogItem = () => {
  const [blogItem, setBlogItem] = useState({});
  const { id } = useParams();

  const getBlogItem = async () => {
    const item = await axios.get(`http://localhost:5000/blog/${id}`);

    setBlogItem(item.data);
  };

  useEffect(() => {
    getBlogItem();
  }, []);

  const { createdAt, image, subtitle, tags, text, title } = blogItem;

  var stringToHTML = function (str) {
    var parser = new DOMParser();
    var doc = parser.parseFromString(str, "text/html");
    return doc.body;
  };

  const content = (
    <div className="blog-item">
      <Navlink2
        colour="#d0491e"
        link="/blog"
        text="&#9756; return to blog grid"
      />
      <Card5
        createdAt={createdAt}
        image={image}
        subtitle={subtitle}
        tags={tags}
        text={stringToHTML(text)}
        title={title}
      />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default BlogItem;
