import { useState } from "react";
import { Link, useLocation } from "react-router-dom";

import nameLogo from "./media/name-logo.svg";

import "./style.min.css";

import Landing from "./components/layout/Landing.js";

import crossIcon from "./media/cross.svg";
import menuIcon from "./media/menu.svg";

const App = () => {
  const [landing, closeLanding] = useState(true);
  const [menu, toggleMenu] = useState(false);

  const location = useLocation();

  const toggleNavbar = () => {
    const navbar = document.getElementById("navbar");

    if (menu) {
      console.log("ran");
      navbar.style.display = "none";
      toggleMenu("false");
    } else {
      navbar.style.display = "block";
      toggleMenu("true");
    }
  };

  return (
    <div className="app">
      <header className="header">
        <div className="header-container">
          <div className="title-box">
            <Link to="/">
              <img className="title-image" src={nameLogo} alt="" />
            </Link>
          </div>
          <img
            className="navbar-icon"
            onClick={toggleNavbar}
            src={menu ? crossIcon : menuIcon}
            alt=""
          />
          <nav className="navbar" id="navbar">
            <div className="navbar-container">
              <Link
                className={`nav-link ${
                  location.pathname == "/work" && "nav-link-active"
                }`}
                onClick={toggleNavbar}
                to="/work"
              >
                Work
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/skills" && "nav-link-active"
                }`}
                onClick={toggleNavbar}
                to="/skills"
              >
                Skills
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/about" && "nav-link-active"
                }`}
                onClick={toggleNavbar}
                to="/about"
              >
                About
                <div className="nav-link-underline"></div>
              </Link>
              <Link
                className={`nav-link ${
                  location.pathname == "/contact" && "nav-link-active"
                }`}
                onClick={toggleNavbar}
                to="/contact"
              >
                Contact
                <div className="nav-link-underline"></div>
              </Link>
            </div>
          </nav>
        </div>
      </header>
      {landing && (
        <Landing
          onClick={() => {
            closeLanding(false);
          }}
        />
      )}
    </div>
  );
};

export default App;
