// import dependencies
import { useState } from "react";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links";
import PrivateRoute from "../../utils/PrivateRoute";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    body: "",
    image: "",
    subtitle: "",
    tags: "",
    title: "",
  });

  const { body, image, subtitle, tags, title } = formData;

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const content = (
    <>
      <form onSubmit={handleSubmit}>
        <Input1 label="title" />
        <Input1 label="subtitle" />
        <Input2 label="body" />
        <Input1 label="tags" />
        <Input3 label="image" />
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 text="return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} title="New Blog" />;
};

export default CreateBlog;
