// import dependencies
import { useState } from "react";
import FileBase from "react-file-base64";

// import components
// utils
import { Button1 } from "../../utils/buttons";
import { Input1, Input2, Input3, Input4, Input5 } from "../../utils/inputs";
import { Navlink2 } from "../../utils/links.js";
import PrivateRoute from "../../utils/PrivateRoute.js";

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    category: "",
    codeLink: "",
    image: "",
    logos: [],
    siteLink: "",
    subtitle: "",
    text: "",
    title: "",
  });

  const {
    codeLink,
    category,
    image,
    logos,
    siteLink,
    subtitle,
    text,
    title,
  } = formData;

  const handleChange = (name) => (e) =>
    setFormData({ ...formData, [name]: e.target.value });

  const handleCheckClick = (i) => {
    let currentArray = logos;

    if (currentArray.includes(i)) {
      const index = currentArray.indexOf(i);

      currentArray.splice(index, 1);
    } else {
      currentArray.push(i);

      setFormData({ ...formData, logos: currentArray });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <>
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-body">
          <Input1 label="title" onChange={handleChange("title")} />
          <Input1 label="subtitle" onChange={handleChange("subtitle")} />
          <Input5
            onChange={handleChange("category")}
            options={["site", "app", "game", "misc"]}
          />
          <Input1 label="codeLink" onChange={handleChange("codeLink")} />
          <Input1 label="siteLink" onChange={handleChange("siteLink")} />
          <Input2 label="text" onChange={handleChange("text")} />
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) => setFormData({ ...formData, image: base64 })}
          />
          <div className="checkboxes">
            <Input4
              onClick={() => {
                handleCheckClick("html");
              }}
              text="html"
            />
            <Input4
              onClick={() => {
                handleCheckClick("css");
              }}
              text="css"
            />
            <Input4
              onClick={() => {
                handleCheckClick("sass");
              }}
              text="sass"
            />
            <Input4
              onClick={() => {
                handleCheckClick("less");
              }}
              text="less"
            />
            <Input4
              onClick={() => {
                handleCheckClick("psql");
              }}
              text="psql"
            />
            <Input4
              onClick={() => {
                handleCheckClick("mongo");
              }}
              text="mongo"
            />
            <Input4
              onClick={() => {
                handleCheckClick("express");
              }}
              text="express"
            />
            <Input4
              onClick={() => {
                handleCheckClick("react");
              }}
              text="react"
            />
            <Input4
              onClick={() => {
                handleCheckClick("react-native");
              }}
              text="react-native"
            />
            <Input4
              onClick={() => {
                handleCheckClick("node");
              }}
              text="node"
            />
            <Input4
              onClick={() => {
                handleCheckClick("python");
              }}
              text="python"
            />
            <Input4
              onClick={() => {
                handleCheckClick("webpack");
              }}
              text="webpack"
            />
          </div>
        </div>
        <Button1 text="SUBMIT" />
      </form>
      <Navlink2 colour="#d0491e" text="&#9756; return to dashboard" />
    </>
  );

  return <PrivateRoute content={content} />;
};

export default CreateBlog;
