import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Slide from "react-reveal/Slide";

import "./style.min.css";

import { Navlink1 } from "../utils/links.js";

import nameLogo from "../../media/name-logo.svg";

import crossIcon from "../../media/cross.svg";
import menuIcon from "../../media/menu.svg";

const Header = () => {
  const [menu, toggleMenu] = useState(false);

  const location = useLocation();

  const toggleNavbar = () => {
    const navbar = document.getElementById("navbar");

    if (navbar.style.display === "block") {
      navbar.style.display = "none";
      toggleMenu(false);
    } else {
      navbar.style.display = "block";
      toggleMenu(true);
    }
  };

  return (
    <Slide down>
      <header
        className="header"
        style={{
          maxHeight: `${window.innerHeight * 0.12}px`,
          minHeight: `${window.innerHeight * 0.12}px`,
        }}
      >
        <div className="header-container">
          <div className="title-box" onClick={menu ? toggleNavbar : null}>
            <Link to="/">
              <img className="title-image" src={nameLogo} alt="" />
            </Link>
          </div>
          <img
            className="navbar-icon"
            onClick={toggleNavbar}
            src={menu ? crossIcon : menuIcon}
            alt=""
          />
          <nav className="navbar" id="navbar">
            <div className="navbar-container">
              <Navlink1 link="/" text="work" toggleNavbar={toggleNavbar} />
              <Navlink1
                link="/skills"
                text="skills"
                toggleNavbar={toggleNavbar}
              />
              <Navlink1
                link="/about"
                text="about"
                toggleNavbar={toggleNavbar}
              />
              <Navlink1 link="/blog" text="blog" toggleNavbar={toggleNavbar} />
              <Navlink1
                link="/contact"
                text="contact"
                toggleNavbar={toggleNavbar}
              />
            </div>
          </nav>
        </div>
      </header>
    </Slide>
  );
};

export default Header;
