// import dependencies
import { Link, useLocation } from "react-router-dom";

import { capitalise } from "../helpers";

export const Link1 = ({ image, sitename, to }) => (
  <a className="link" href={to} target="_blank">
    <div className="link-hover">Visit my {capitalise(sitename)} page</div>
    <i className={`link-icon fa-brands fa-${sitename}`}></i>
  </a>
);

export const Navlink1 = ({ link, text, toggleNavbar = toggleNavbar }) => {
  const page = location.pathname.substring(1, 5);
  const activePage =
    page === text.substring(0, 4) || (text === "work" && page === "");

  return (
    <Link
      className={`nav-link ${activePage && "nav-link-active"}`}
      to={link}
      onClick={screen.width <= 767 && toggleNavbar}
    >
      {capitalise(text)}
      <div className="nav-link-underline"></div>
    </Link>
  );
};

export const Navlink2 = ({ link, text }) => (
  <Link className="nav-link nav-link2" to={link}>
    {text}
  </Link>
);
