// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// import components
// utils
import { Card3 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

const BlogItem = () => {
  const [blogItem, setBlogItem] = useState({});
  const { id } = useParams();

  const getBlogItem = async () => {
    const item = await axios.get(`http://localhost:5000/blog/${id}`);

    setBlogItem(item.data);
  };

  useEffect(() => {
    getBlogItem();
  }, []);

  const pageHeight = window.innerHeight - (window.innerWidth < 767 ? 60 : 95);

  const {
    category,
    codeLink,
    image,
    logos,
    siteLink,
    subtitle,
    text,
    title,
  } = blogItem;

  const content = (
    <div className="blog-item" style={{ height: `${pageHeight}px` }}>
      <Navlink2 colour="#d0491e" link="/" text="&#9756; return to blog grid" />
      {
        <Card3
          codeLink={codeLink}
          image={image}
          logos={logos}
          siteLink={siteLink}
          subtitle={subtitle}
          text={text}
          title={title}
        />
      }
    </div>
  );

  return <PublicRoute content={content} />;
};

export default BlogItem;
