// import dependencies
import { useState } from "react";

// import utils
import PublicRoute from "../../utils/PublicRoute.js";
import { Button3 } from "../../utils/buttons";
import { Input1, Input2 } from "../../utils/inputs";

// import helpers

// import styles
import "./style.min.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    body: "",
    email: "",
    phone: "",
    title: "",
  });
  const [state, setState] = useState({
    btnDisabled: true,
    btnText: "submit",
    error: "",
  });

  const { body, email, phone, title } = formData;
  const { btnDisabled, btnText, error } = state;

  const handleChange = (name) => (e) => {
    setFormData({ ...formData, [name]: e.target.value });
  };

  const handleMouseOver = () => {
    console.log("mouse over");
    if (title == "" || email == "" || phone == "" || body == "") {
      setState({ ...state, error: "please complete all fields" });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(formData);
  };

  const content = (
    <div className="contact">
      <form onSubmit={handleSubmit}>
        <Input1
          label="email title"
          onChange={handleChange("title")}
          placeholder="enter title"
          value={title}
        />
        <Input1
          label="your email address"
          onChange={handleChange("email")}
          placeholder="enter email"
          value={email}
        />
        <Input1
          label="your phone number"
          onChange={handleChange("phone")}
          placeholder="enter phone number"
          value={phone}
        />
        <Input2
          label="email body"
          onChange={handleChange("body")}
          placeholder="enter email body"
          value={body}
        />
        <Button3
          text={btnText}
          disabled={btnDisabled}
          error={error}
          onMouseOver={() => {
            handleMouseOver;
          }}
        />
      </form>
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Contact;
