// import dependencies
import { useEffect, useState } from "react";

// import components
// utils
import { Card3 } from "../../utils/cards.js";
import { Navlink2 } from "../../utils/links.js";
import PublicRoute from "../../utils/PublicRoute.js";

const WorkItem = () => {
  const [workItem, getWorkItem] = useState();
  const pageHeight = window.innerHeight - (window.innerWidth < 767 ? 60 : 95);

  const content = (
    <div className="work-item" style={{ height: `${pageHeight}px` }}>
      <Navlink2 colour="#d0491e" link="/" text="&#9756; return to work grid" />
    </div>
  );

  return <PublicRoute content={content} />;
};

export default WorkItem;
