// import dependencies
import { Fade } from "react-reveal";
import { Link } from "react-router-dom";

// import helpers
import { capitalise } from "../helpers";

// import components
import { Button1, Button2 } from "./buttons";

// import styles
import "./style.min.css";

export const Card1 = ({ category, codeLink, siteLink, title }) => (
  <Fade delay={600} up>
    <div className="card card1">
      <Link to={`/work/${title}`}>
        <div className="card1-image-container">
          <div className="card1-veneer">
            <p className="card1-veneer-text">MORE</p>
            <p className="card1-veneer-text">INFO</p>
          </div>
        </div>
      </Link>
      <div className="card1-text-container">
        <h3 className="card-title">{capitalise(title)}</h3>
        <p className="card-subtitle">{category}</p>
        <Button1 to={siteLink} text="VIEW PAGE" />
        <Button2 to={codeLink} text="VIEW CODE" />
      </div>
    </div>
  </Fade>
);
