import {
  CREATE_BLOG,
  DELETE_BLOG,
  READ_BLOG,
  READ_BLOG_ITEM,
  UPDATE_BLOG,
} from "./blogTypes";

export default (blog = [], action) => {
  switch (action.type) {
    case CREATE_BLOG:
      return blog;
    case DELETE_BLOG:
      return blog;
    case READ_BLOG:
      return blog;
    case READ_BLOG_ITEM:
      return blog;
    case UPDATE_BLOG:
      return blog;
    default:
      return blog;
  }
};
