// import dependencies
import { useState } from "react";

// import utils
import PublicRoute from "../utils/PublicRoute.js";
import { Button3 } from "../utils/buttons";
import { Input1, Input2 } from "../utils/inputs";

const Contact = () => {
  const [formData, setFormData] = useState({
    title: "",
    email: "",
    body: "",
  });
  const [state, setState] = useState({
    btnText: "submit",
  });

  const { title, email, body } = formData;
  const { btnText } = state;

  const content = (
    <>
      <form className="form">
        <Input1 placeholder="enter title" value={title} />
        <Input1 placeholder="enter email" value={email} />
        <Input2 placeholder="enter email body" value={body} />
        <Button3 text={btnText} />
      </form>
    </>
  );

  return <PublicRoute content={content} />;
};

export default Contact;
